import React, { useState, useEffect, useContext } from 'react';
import SearchBar from '../components/SearchBar';
import CategoryFilter from '../components/CategoryFilter';
import ItemCard from '../components/ItemCard';
import LoadingSpinner from '../components/LoadingSpinner';
import apiService from '../services/api';
import { AuthContext } from '../context/AuthContext';

const LostItems = () => {
  const [items, setItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { checkServerStatus } = useContext(AuthContext);
  
  useEffect(() => {
    const fetchItems = async () => {
      try {
        // Check server availability
        const serverAvailable = await checkServerStatus();
        if (!serverAvailable) {
          return; // AuthContext will handle redirection
        }
        
        setLoading(true);
        const response = await apiService.getAllItems();
        const data = response.data.data || response.data;
        
        setItems(data);
        setFilteredItems(data);
        
        // Extract unique categories
        const uniqueCategories = [...new Set(data.map(item => item.category).filter(Boolean))];
        setCategories(uniqueCategories);
        
        setLoading(false);
      } catch (err) {
        console.error('Error fetching items:', err);
        setError('Failed to load items');
        setLoading(false);
      }
    };
    
    fetchItems();
  }, [checkServerStatus]);
  
  // Handle search
  const handleSearch = (searchTerm) => {
    if (!searchTerm.trim()) {
      // Reset to all items if search is empty
      setFilteredItems(selectedCategory 
        ? items.filter(item => item.category === selectedCategory)
        : items
      );
      return;
    }
    
    // Client-side search for simplicity
    const searchResults = items.filter(item => {
      const searchLower = searchTerm.toLowerCase();
      return (
        (item.name && item.name.toLowerCase().includes(searchLower)) ||
        (item.description && item.description.toLowerCase().includes(searchLower)) ||
        (item.category && item.category.toLowerCase().includes(searchLower)) ||
        (item.location && item.location.toLowerCase().includes(searchLower))
      );
    });
    
    // Apply category filter to search results if needed
    if (selectedCategory) {
      setFilteredItems(searchResults.filter(item => item.category === selectedCategory));
    } else {
      setFilteredItems(searchResults);
    }
  };
  
  // Handle category filter
  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    
    if (category) {
      // If there's a search term already applied, filter the current filtered items
      setFilteredItems(items.filter(item => item.category === category));
    } else {
      setFilteredItems(items);
    }
  };
  
  return (
    <div className="bg-white">
      <div className="max-w-7xl mx-auto px-4 py-8 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold text-secondary-900 mb-8">Lost Items</h1>
        
        <div className="mb-8">
          <div className="flex flex-col md:flex-row gap-4 md:items-center md:justify-between">
            <div className="w-full md:w-1/2 lg:w-2/5">
              <SearchBar onSearch={handleSearch} />
            </div>
            
            <div className="flex items-center space-x-2">
              <span className="text-sm text-secondary-700">Filter by:</span>
              <CategoryFilter 
                categories={categories} 
                selectedCategory={selectedCategory}
                onSelectCategory={handleCategorySelect}
              />
            </div>
          </div>
        </div>
        
        {loading ? (
          <div className="flex justify-center items-center h-64">
            <LoadingSpinner size="large" />
          </div>
        ) : error ? (
          <div className="text-center py-12">
            <p className="text-red-500">{error}</p>
            <button 
              onClick={() => window.location.reload()}
              className="mt-4 text-primary-600 hover:text-primary-700"
            >
              Try Again
            </button>
          </div>
        ) : (
          <>
            {filteredItems.length > 0 ? (
              <>
                <p className="text-sm text-secondary-500 mb-4">
                  Showing {filteredItems.length} {filteredItems.length === 1 ? 'item' : 'items'}
                  {selectedCategory ? ` in ${selectedCategory}` : ''}
                </p>
                
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                  {filteredItems.map((item) => (
                    <ItemCard key={item._id || item.id} item={item} />
                  ))}
                </div>
              </>
            ) : (
              <div className="text-center py-16">
                <svg 
                  className="mx-auto h-12 w-12 text-secondary-400" 
                  xmlns="http://www.w3.org/2000/svg" 
                  fill="none" 
                  viewBox="0 0 24 24" 
                  stroke="currentColor"
                >
                  <path 
                    strokeLinecap="round" 
                    strokeLinejoin="round" 
                    strokeWidth={2} 
                    d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" 
                  />
                </svg>
                <h3 className="mt-2 text-lg font-medium text-secondary-900">No items found</h3>
                <p className="mt-1 text-sm text-secondary-500">
                  {selectedCategory 
                    ? `No items found in the ${selectedCategory} category.` 
                    : 'No items match your search criteria.'}
                </p>
                <button
                  onClick={() => {
                    setSelectedCategory(null);
                    setFilteredItems(items);
                  }}
                  className="mt-4 text-primary-600 hover:text-primary-700 font-medium"
                >
                  Clear filters
                </button>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default LostItems;
