import React, { useState } from 'react';
import { toast } from 'react-toastify';
import { claimItem } from '../services/itemService';

const ClaimForm = ({ itemId, onClaimSubmitted }) => {
  const [formData, setFormData] = useState({
    studentName: '',
    studentId: '',
    studentYear: '',
    contactNumber: '',
    idProof: null
  });
  const [submitting, setSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleFileChange = (e) => {
    setFormData(prevState => ({
      ...prevState,
      idProof: e.target.files[0]
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Basic validation
    if (!formData.studentName || !formData.studentId || !formData.contactNumber) {
      toast.error('Please fill all required fields');
      return;
    }
    
    setSubmitting(true);
    
    try {
      // Prepare form data for API request
      const claimFormData = new FormData();
      claimFormData.append('studentName', formData.studentName);
      claimFormData.append('studentId', formData.studentId);
      claimFormData.append('studentYear', formData.studentYear);
      claimFormData.append('contactNumber', formData.contactNumber);
      
      if (formData.idProof) {
        claimFormData.append('idProof', formData.idProof);
      }
      
      // In development, we'll simulate the API call
      // TODO: Replace with actual API call in production
      // await claimItem(itemId, claimFormData);
      
      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      // Notify parent component that claim was successful
      if (onClaimSubmitted) {
        onClaimSubmitted();
      }
      
      toast.success('Claim submitted successfully');
      
      // Reset form
      setFormData({
        studentName: '',
        studentId: '',
        studentYear: '',
        contactNumber: '',
        idProof: null
      });
    } catch (error) {
      console.error('Error submitting claim:', error);
      toast.error('Failed to submit claim');
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md my-4">
      <h2 className="text-2xl font-semibold text-gray-800 mb-4">Claim this Item</h2>
      
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="studentName">
            Full Name*
          </label>
          <input
            id="studentName"
            name="studentName"
            type="text"
            value={formData.studentName}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="studentId">
            Student ID*
          </label>
          <input
            id="studentId"
            name="studentId"
            type="text"
            value={formData.studentId}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="studentYear">
            Year of Study
          </label>
          <select
            id="studentYear"
            name="studentYear"
            value={formData.studentYear}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          >
            <option value="">Select Year</option>
            <option value="FE">First Year (FE)</option>
            <option value="SE">Second Year (SE)</option>
            <option value="TE">Third Year (TE)</option>
            <option value="BE">Final Year (BE)</option>
          </select>
        </div>
        
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="contactNumber">
            Contact Number*
          </label>
          <input
            id="contactNumber"
            name="contactNumber"
            type="tel"
            value={formData.contactNumber}
            onChange={handleChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            required
          />
        </div>
        
        <div className="mb-6">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="idProof">
            ID Proof
          </label>
          <input
            id="idProof"
            name="idProof"
            type="file"
            onChange={handleFileChange}
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
          />
          <p className="text-xs text-gray-500 mt-1">
            Please upload an image of your student ID card or any other valid ID
          </p>
        </div>
        
        <div className="flex items-center justify-between">
          <button
            type="submit"
            disabled={submitting}
            className={`w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline ${submitting ? 'opacity-50 cursor-not-allowed' : ''}`}
          >
            {submitting ? 'Submitting...' : 'Submit Claim'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default ClaimForm;
