import React from 'react';
import { useForm } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { useNavigate } from 'react-router-dom';
import { FaLock, FaUser, FaShieldAlt } from 'react-icons/fa';

// Validation Schema using Yup
const schema = yup.object().shape({
  username: yup.string().required('Username is required'),
  password: yup.string().min(6, 'Password must be at least 6 characters').required('Password is required'),
});

const Login = () => {
  const navigate = useNavigate();
  const {
    register,
    handleSubmit,
    formState: { errors }
  } = useForm({
    resolver: yupResolver(schema),
  });

  const onSubmit = (data) => {
    // Hardcoded credentials check
    if (data.username === 'pict_guard' && data.password === 'secure@guard123') {
      navigate('/guard-dashboard');
    } else {
      alert('Invalid username or password');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8 bg-white p-10 rounded-xl shadow-lg">
        <div className="text-center">
          <div className="flex justify-center">
            <div className="bg-blue-100 rounded-full p-3">
              <FaShieldAlt className="h-10 w-10 text-blue-600" />
            </div>
          </div>
          <h2 className="mt-6 text-3xl font-extrabold text-gray-900">Security Login</h2>
        </div>

        <form className="mt-8 space-y-6" onSubmit={handleSubmit(onSubmit)}>
          <div className="space-y-4">
            <div>
              <div className="relative">
                <FaUser className="absolute left-3 top-3 text-gray-400" />
                <input {...register('username')} className="input-field" placeholder="Username" />
              </div>
              {errors.username && <p className="text-red-500 text-xs">{errors.username.message}</p>}
            </div>

            <div>
              <div className="relative">
                <FaLock className="absolute left-3 top-3 text-gray-400" />
                <input {...register('password')} type="password" className="input-field" placeholder="Password" />
              </div>
              {errors.password && <p className="text-red-500 text-xs">{errors.password.message}</p>}
            </div>
          </div>

          <button type="submit" className="btn-primary">Sign in</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
