import React, { useState } from 'react';
import { toast } from 'react-toastify';
import { claimItem } from '../services/itemService';

const ClaimForm = ({ itemId, onClaimSubmitted }) => {
  const [formData, setFormData] = useState({
    studentName: '',
    studentId: '',
    studentYear: '',
    contactNumber: '',
    idProof: null
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      idProof: e.target.files[0]
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Basic validation
    if (!formData.studentName || !formData.studentId || !formData.studentYear || !formData.contactNumber) {
      toast.error('Please fill all required fields');
      return;
    }
    
    setIsSubmitting(true);
    
    try {
      // Create FormData object for file upload
      const claimFormData = new FormData();
      claimFormData.append('studentName', formData.studentName);
      claimFormData.append('studentId', formData.studentId);
      claimFormData.append('studentYear', formData.studentYear);
      claimFormData.append('contactNumber', formData.contactNumber);
      
      if (formData.idProof) {
        claimFormData.append('idProof', formData.idProof);
      }
      
      // For development, we'll simulate a successful claim
      // In production, use the actual API call
      // await claimItem(itemId, claimFormData);
      
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // Notify parent component
      if (onClaimSubmitted) {
        onClaimSubmitted();
      }
    } catch (error) {
      toast.error(error.message || 'Failed to submit claim. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-4 text-blue-800">Claim This Item</h2>
      <p className="mb-4 text-gray-600">
        Please fill out this form to claim the item. You will need to verify your identity with the security guard.
      </p>
      
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="studentName">
            Full Name *
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="studentName"
            name="studentName"
            type="text"
            placeholder="Your full name"
            value={formData.studentName}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="studentId">
            Student ID / Roll Number *
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="studentId"
            name="studentId"
            type="text"
            placeholder="Your student ID or roll number"
            value={formData.studentId}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="studentYear">
            Study Year *
          </label>
          <select
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="studentYear"
            name="studentYear"
            value={formData.studentYear}
            onChange={handleChange}
            required
          >
            <option value="">Select your year</option>
            <option value="First Year">First Year</option>
            <option value="Second Year">Second Year</option>
            <option value="Third Year">Third Year</option>
            <option value="Fourth Year">Fourth Year</option>
          </select>
        </div>
        
        <div className="mb-4">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="contactNumber">
            Contact Number *
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="contactNumber"
            name="contactNumber"
            type="tel"
            placeholder="Your contact number"
            value={formData.contactNumber}
            onChange={handleChange}
            required
          />
        </div>
        
        <div className="mb-6">
          <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="idProof">
            ID Proof (Optional)
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="idProof"
            name="idProof"
            type="file"
            accept="image/*"
            onChange={handleFileChange}
          />
          <p className="text-xs text-gray-500 mt-1">
            Upload a photo of your student ID for faster verification
          </p>
        </div>
        
        <div className="flex items-center justify-between">
          <button
            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline w-full"
            type="submit"
            disabled={isSubmitting}
          >
            {isSubmitting ? 'Submitting...' : 'Submit Claim'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default ClaimForm;
