import { createContext, useState, useEffect, useCallback } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [serverAvailable, setServerAvailable] = useState(true);

  // Check server availability
  const checkServerStatus = useCallback(async () => {
    try {
      await axios.get('http://localhost:5000/api/test');
      setServerAvailable(true);
      return true;
    } catch (error) {
      console.log('Server unavailable:', error.message);
      setServerAvailable(false);
      return false;
    }
  }, []);

  // Validate token with server
  const validateToken = useCallback(async (token) => {
    try {
      // Just check if server is available - in a real app, you'd validate the token
      const isAvailable = await checkServerStatus();
      if (!isAvailable) {
        return false;
      }
      return true;
    } catch (error) {
      console.error('Error validating token:', error);
      return false;
    }
  }, [checkServerStatus]);

  // Check token expiration
  const isTokenExpired = useCallback((userData) => {
    if (!userData || !userData.createdAt) return true;
    
    const tokenCreationTime = new Date(userData.createdAt).getTime();
    const currentTime = new Date().getTime();
    
    // Token expires after 8 hours (28800000 ms)
    const expirationTime = 8 * 60 * 60 * 1000;
    
    return currentTime - tokenCreationTime > expirationTime;
  }, []);

  // Login function to be used by Login component
  const login = useCallback((username, password) => {
    return new Promise((resolve, reject) => {
      // Check against hardcoded credentials
      if (username === "pict_guard" && password === "secure@guard123") {
        // Create a mock token for development
        const userData = {
          username: "pict_guard",
          role: "guard",
          token: "dev-jwt-token-for-guard-auth", // In production, get from backend
          createdAt: new Date().toISOString() // Add creation time for expiration check
        };
        
        // Store in localStorage for API requests
        localStorage.setItem('token', userData.token);
        localStorage.setItem('user', JSON.stringify(userData));
        
        setUser(userData);
        resolve(userData);
      } else {
        reject(new Error("Invalid credentials"));
      }
    });
  }, []);

  const logout = useCallback(() => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    // Add a redirect to home page if needed
    window.location.href = '/';
  }, []);

  const isAuthenticated = useCallback(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    
    if (!token || !userData) {
      return false;
    }
    
    try {
      const parsedUserData = JSON.parse(userData);
      if (isTokenExpired(parsedUserData)) {
        return false;
      }
      return true;
    } catch (e) {
      return false;
    }
  }, [isTokenExpired]);

  const isGuard = useCallback(() => {
    return user?.role === 'guard';
  }, [user]);

  // Initialize auth state from localStorage and validate with server
  useEffect(() => {
    const initAuth = async () => {
      setLoading(true);
      
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');
      
      if (token && userData) {
        try {
          // Parse user data
          const parsedUserData = JSON.parse(userData);
          
          // Check if token is expired
          if (isTokenExpired(parsedUserData)) {
            console.log('Token expired, logging out');
            localStorage.removeItem('token');
            localStorage.removeItem('user');
            setUser(null);
          } else {
            // Validate token with server
            const isValid = await validateToken(token);
            
            if (isValid) {
              setUser(parsedUserData);
            } else {
              console.log('Invalid token, logging out');
              localStorage.removeItem('token');
              localStorage.removeItem('user');
              setUser(null);
            }
          }
        } catch (error) {
          // Invalid user data in localStorage
          console.error('Error initializing auth:', error);
          localStorage.removeItem('token');
          localStorage.removeItem('user');
          setUser(null);
        }
      }
      
      setLoading(false);
    };
    
    initAuth();
  }, [isTokenExpired, validateToken]);

  return (
    <AuthContext.Provider 
      value={{ 
        user,
        loading,
        serverAvailable,
        login,
        logout,
        isAuthenticated,
        isGuard,
        isSecurityGuard: isGuard // Alias for backward compatibility
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
