import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FaCalendarAlt, FaMapMarkerAlt, FaTag, FaInfoCircle, FaUser } from 'react-icons/fa';
import ClaimForm from '../components/ClaimForm';
import LoadingSpinner from '../components/LoadingSpinner';
import { AuthContext } from '../context/AuthContext';
import apiService from '../services/api';

const ItemDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { isGuard, checkServerStatus } = useContext(AuthContext);
  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showClaimForm, setShowClaimForm] = useState(false);

  useEffect(() => {
    const fetchItemDetails = async () => {
      try {
        // Check if server is available
        const serverAvailable = await checkServerStatus();
        if (!serverAvailable) {
          return; // AuthContext will redirect to server-unavailable
        }
        
        setLoading(true);
        const response = await apiService.getItemById(id);
        
        if (response.data && response.data.data) {
          setItem(response.data.data);
        } else if (response.data) {
          setItem(response.data);
        } else {
          throw new Error('Invalid response format');
        }
        
        setLoading(false);
      } catch (err) {
        console.error('Error fetching item details:', err);
        setError('Failed to load item details. Please try again.');
        setLoading(false);
      }
    };

    if (id) {
      fetchItemDetails();
    } else {
      setError('Item ID is missing');
      setLoading(false);
    }
  }, [id, checkServerStatus]);

  const handleClaim = () => {
    setShowClaimForm(true);
  };

  const handleCloseForm = () => {
    setShowClaimForm(false);
  };

  const handleClaimSubmitted = () => {
    // Update the item status in the UI
    setItem(prev => ({ ...prev, status: 'claimed' }));
    setShowClaimForm(false);
    toast.success('Claim submitted successfully! Please visit the security office to collect your item.');
  };

  const handleMarkAsDelivered = async () => {
    try {
      // Check if server is available
      const serverAvailable = await checkServerStatus();
      if (!serverAvailable) {
        return; // AuthContext will redirect to server-unavailable
      }
      
      await apiService.markAsDelivered(id);
      
      setItem((prevItem) => ({
        ...prevItem,
        status: 'delivered',
      }));
      
      toast.success('Item marked as delivered successfully');
    } catch (err) {
      console.error('Error marking item as delivered:', err);
      toast.error('Failed to mark item as delivered');
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    try {
      return new Date(dateString).toLocaleDateString(undefined, options);
    } catch (e) {
      return 'Invalid date';
    }
  };

  // Function to get the full image URL
  const getImageUrl = (imagePath) => {
    if (!imagePath) return '/assets/images/placeholder.png';
    
    // If it's a full URL already, return as is
    if (imagePath.startsWith('http')) return imagePath;
    
    // Otherwise, prepend the server URL
    return `http://localhost:5000${imagePath}`;
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  if (error) {
    return (
      <div className="container mx-auto px-4 py-16">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-red-600 mb-4">Error</h2>
          <p className="text-gray-700">{error}</p>
          <button
            onClick={() => navigate('/lost-items')}
            className="mt-6 bg-blue-600 text-white py-2 px-6 rounded-lg hover:bg-blue-700 transition duration-300"
          >
            Back to Items
          </button>
        </div>
      </div>
    );
  }

  if (!item) {
    return <LoadingSpinner />;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <ToastContainer position="top-right" autoClose={3000} />
      
      <div className="mb-6">
        <button
          onClick={() => navigate('/lost-items')}
          className="text-blue-600 hover:text-blue-800 flex items-center"
        >
          <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 19l-7-7m0 0l7-7m-7 7h18"></path>
          </svg>
          Back to Lost Items
        </button>
      </div>
      
      <div className="bg-white rounded-lg shadow-lg overflow-hidden">
        <div className="md:flex">
          <div className="md:w-1/2">
            <div className="h-80 bg-gray-200 flex items-center justify-center overflow-hidden">
              {item.image ? (
                <img 
                  src={getImageUrl(item.image)} 
                  alt={item.name} 
                  className="w-full h-full object-cover"
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = '/assets/images/placeholder.png';
                  }}
                />
              ) : (
                <div className="text-gray-400">No image available</div>
              )}
            </div>
          </div>
          
          <div className="md:w-1/2 p-6">
            <div className={`inline-block px-3 py-1 rounded-full text-sm font-semibold mb-4 ${
              item.status === 'available' 
                ? 'bg-green-100 text-green-800' 
                : item.status === 'claimed' 
                ? 'bg-yellow-100 text-yellow-800' 
                : 'bg-red-100 text-red-800'
            }`}>
              {item.status ? item.status.charAt(0).toUpperCase() + item.status.slice(1) : 'Unknown'}
            </div>
            
            <h1 className="text-2xl font-bold text-gray-800 mb-2">{item.name || 'Unnamed Item'}</h1>
            
            <div className="mb-6">
              <div className="flex items-center text-gray-600 mb-2">
                <FaTag className="mr-2" />
                <span>Category: </span>
                <span className="font-medium ml-1">{item.category || 'Uncategorized'}</span>
              </div>
              
              <div className="flex items-center text-gray-600 mb-2">
                <FaCalendarAlt className="mr-2" />
                <span>Found on: </span>
                <span className="font-medium ml-1">{formatDate(item.foundDate)}</span>
              </div>
              
              <div className="flex items-center text-gray-600 mb-2">
                <FaMapMarkerAlt className="mr-2" />
                <span>Location: </span>
                <span className="font-medium ml-1">{item.location || 'Unknown'}</span>
              </div>
              
              {item.claimedBy && (
                <div className="flex items-center text-gray-600 mb-2">
                  <FaUser className="mr-2" />
                  <span>Claimed by: </span>
                  <span className="font-medium ml-1">
                    {item.claimedBy.studentName || item.claimedBy.name || 'Unknown'} 
                    {item.claimedBy.rollNumber ? `(${item.claimedBy.rollNumber})` : ''}
                  </span>
                </div>
              )}
            </div>
            
            <div className="mb-6">
              <h3 className="flex items-center text-gray-700 font-semibold mb-2">
                <FaInfoCircle className="mr-2" />
                Description
              </h3>
              <p className="text-gray-600">{item.description || 'No description provided.'}</p>
            </div>
            
            {item.status === 'available' && !isGuard() && !showClaimForm && (
              <button
                onClick={handleClaim}
                className="w-full bg-blue-600 text-white py-3 rounded-lg hover:bg-blue-700 transition duration-300"
              >
                Claim This Item
              </button>
            )}
            
            {item.status === 'claimed' && isGuard() && (
              <button
                onClick={handleMarkAsDelivered}
                className="w-full bg-green-600 text-white py-3 rounded-lg hover:bg-green-700 transition duration-300"
              >
                Mark as Delivered
              </button>
            )}
          </div>
        </div>
      </div>
      
      {/* Claim Form */}
      {showClaimForm && (
        <div className="mt-8 bg-gray-50 p-6 rounded-lg">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-semibold text-gray-800">Claim Form</h2>
            <button 
              onClick={handleCloseForm}
              className="text-gray-500 hover:text-gray-700"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
              </svg>
            </button>
          </div>
          
          <ClaimForm 
            itemId={id} 
            onClaimSubmitted={handleClaimSubmitted}
          />
        </div>
      )}
    </div>
  );
};

export default ItemDetails;
